import React, {useState} from "react";

function CreateArea() {
  const [state, setState]= useState({title: "", content: ""});

  return (
    <div>
      <form onSubmit={handleClick}>
        <input name="title" onChange={handleChange} placeholder="Title" value={state.title}/>
        <textarea name="content" onChange={handleChange} placeholder="Take a note..." rows="3" value={state.content}/>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
