import React, {useState} from "react";

function CreateArea() {
  const [state, setState]= useState({title: "", content: ""});
  const [items, setItems] = useState([]);

  function handleChange(evt)
  {const [name, value]= evt.target;
   setState((preVal) => {
    if(name==="title")
    {return {title: value, content: preVal}};
    if(name==="content")
    {return {title: preVal, content: value}};
   });
  }

  function handleClick()
  {setItems((preVal) => [...preVal, {title: state.title, content: state.content}]);}

  return (
    <div>
      <form>
        <input name="title" placeholder="Title" onChangevalue={state.title}/>
        <textarea name="content" placeholder="Take a note..." rows="3" value={state.content}/>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
