import React from "react";
import AddIcon from '@material-ui/icons/Add';

function CreateArea(p) {

  return (
    <div>
    <form className="create-note" onSubmit={(evt) => {evt.preventDefault();}}>
        <input name="title" onChange={p.handleChange} placeholder="Title" value={p.state.title} />
        <textarea name="content" onChange={p.handleChange} placeholder="Take a note..." rows="3" value={p.state.content}/>
        <button onClick={() => {p.onClick(p.state);}}><AddIcon fontSize/></button>
        </form>
    </div>
  );
}

export default CreateArea;
