  return (
    <div>
      <Header />
      <CreateArea onClick={handleClick} handleChange={handleChange} state={state}/>
      {items.map((item, key) => <Note key={key} id={key} title={item.title} content={item.content} onDelete={deleteOne} onEdit={editOne}/> )}
      <Footer />
    </div>
  );
}

export default App;
