import React from "react";
import AddIcon from '@material-ui/icons/Add';
import { Zoom } from '@material-ui/core';
import { Fab } from '@material-ui/core';

function CreateArea(p) {
  const [state, setState]= React.useState(false);

  function handleClick()
  {setState(true);}

  return (
    <div>
    <form className="create-note" onSubmit={(evt) => {evt.preventDefault();}}>
        {state && <input name="title" onChange={p.handleChange} placeholder="Title" value={p.state.title}/>}
        <textarea name="content" onChange={p.handleChange} onClick={handleClick} placeholder="Take a note..."  value={p.state.content}/>

        <Zoom in={state} >
        <Fab onClick={() => {p.onClick(p.state);}}>
        <AddIcon/>
        </Fab>
        </Zoom>

        </form>
    </div>
  );
}

export default CreateArea;
