import React from "react";

function CreateArea(p) {
  const [state, setState]= React.useState({title:"", content:"" });

  function handleChange(evt)
  { const {name, value} = evt.target;
    setState((preVal) => ({...preVal, [name]: value}));}


  return (
    <div>
    <form onSubmit={(evt) => {evt.preventDefault(); p.on}}>
        <input name="title" onChange={handleChange} placeholder="Title" value={state.title} />
        <textarea name="content" onChange={handleChange} placeholder="Take a note..." rows="3" value={state.content}/>
        <button onClick={() => {p.onClick(state);
         setState({title: "", content: ""});}}>Add</button>
        </form>
    </div>
  );
}

export default CreateArea;
