import React, {
  useState
} from "react";

function CreateArea(props) {
  const [state, setState] = useState({
    title: "",
    content: ""
  });

  function handleChange(evt) {
    const {
      name,
      value
    } = evt.target;
    setState((preVal) => ({
      ...preVal,
      [name]: value
    }));
  }

  return ( <
    div >
    <
    form onSubmit = {
      () => {
        props.onClick(state);
        setState({
          title: "",
          content: ""
        });
      }
    } >
    <
    input name = "title"
    onChange = {
      handleChange
    }
    placeholder = "Title"
    value = {
      state.title
    }
    /> <
    textarea name = "content"
    onChange = {
      handleChange
    }
    placeholder = "Take a note..."
    rows = "3"
    value = {
      state.content
    }
    /> <
    button > Add < /button> <
    /form> <
    /div>
  );
}

export default CreateArea;
